jQuery(document).ready(function () {
   
// HOME VIDEO POPUP
   insightInitLightGallery2( jQuery( '.header-right .header-button' ) ); 
  
// PORTFOLIO CUSTOM LIGHTBOX
  if (jQuery(".single-portfolio .tm-light-gallery").length > 0) {
    jQuery( '.tm-light-gallery' ).each( function() {
  		jQuery(this).data('lightGallery').destroy(true);
        jQuery(this).removeClass( "tm-light-gallery" );
    } );
  }

  jQuery( '.my-lightbox' ).each( function() {
    insightInitLightGallery2( jQuery( this ) );  
  } );
  

function insightInitLightGallery2 ( $gallery ) {
        var _download   = (
                    $insight.light_gallery_download === '1'
            ),
            _autoPlay   = (
                    $insight.light_gallery_auto_play === '1'
            ),
            _zoom       = (
                    $insight.light_gallery_zoom === '1'
            ),
            _fullScreen = (
                    $insight.light_gallery_full_screen === '1'
            ),
            _share      = (
                    $insight.light_gallery_share === '1'
            ),
            _thumbnail  = (
                    $insight.light_gallery_thumbnail === '1'
            );
        var options = {
                selector: '.zoom',
                thumbnail: _thumbnail,
                download: _download,
                autoplay: _autoPlay,
                zoom: _zoom,
                share: _share,
                fullScreen: _fullScreen,
                hash: false,
                animateThumb: false,
                showThumbByDefault: false,
                getCaptionFromTitleOrAlt: false
        };
        $gallery.lightGallery( options );
}

                                    
});                                    

